import { FilmComment } from 'src/app/domain/models';
import { Injectable, Input } from '@angular/core';
import { Film, Actor } from '../domain/models';

@Injectable({
  providedIn: 'root'
})
export class FilmDataService {

  private films: Film[] = [
    {
      id:1,
      title: 'Matrix',
      description: 'Action Sci-Fi',
      actors: [{name:'Keanu Reeves',role:'Neo'},{name:'	Laurence Fishburne',role:'Morpheus'},{name:'Carrie-Anne Moss',role:'Trinity'}],
      averageMark: 0,
      marks: [],
      images: ['data:image/jpeg;base64,'],
      isFavourite:true,
      comments:[]
    },
    {
      id:2,
      title: 'Avatar',
      description: 'Action Adventure Fantasy',
      actors: [{name:'Sam Worthington',role:'Jake Sully'},{name:'Zoe Saldana',role:'Neytiri'}],
      averageMark: 0,
      marks: [],
      images: ['data:image/jpeg;base64,', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRFgUodpFKfs1aDLHcNwswNi0tq50qM4I92Xg&usqp=CAU'],
      isFavourite:false,
      comments:[]
    },
    {
      id:3,
      title: 'Superman',
      description: 'Action Adventure Sci-Fi',
      actors: [{name:'Marlon Brando',role:'Jor-El'},{name:'Gene Hackman',role:'Lex Luthor'}, {name:'Christopher Reeve',role:'Superman / Clark Kent'}],
      averageMark: 0,
      marks: [],
      images: ['https://www.denofgeek.com/wp-content/uploads/2022/05/superman-78-dc-comics.jpeg',
    'https://cdn.vox-cdn.com/thumbor/MgpH_uOL3gMfjFotLKioI6Sm3lE=/181x404:2802x2020/1200x800/filters:focal(1260x1260:1740x1740)/cdn.vox-cdn.com/uploads/chorus_image/image/70004345/Superman_New_Mission_Statement.0.jpg','https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTCMZ6f_nVcgasIoAil1DfVMm2qR1EnqcClyA&usqp=CAU','https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcScOobFZHa7JX0SHuoVyDBapzpxehB5kH1ZDg&usqp=CAU'],
    isFavourite:false,
    comments:[]
    }
  ]

private favourites: Film[] = this.films.filter(f => f.isFavourite);


  constructor() { }

  getAll(){
    return this.films;
  }

  getById(filmId: number): Film | undefined{
      return this.films.find(film => film.id === filmId);
  }

  addCommentToFilm(comment: FilmComment, filmId: number) {
    const film = this.films.find( f => f.id === filmId);
    if(film){
      if(!film.comments){
        film.comments = [];
      }
      comment.id = film.comments.reduce((prev, curr) => Math.max(prev, curr.id), 0) + 1;
       film.marks?.push(comment.mark);
       film.comments.push(comment);
      console.log(film.comments);
    }
  }


  addFilmToFavourite(filmId: number) {
    const film = this.films.find(f => f.id === filmId);
      if(film){
      if (!film.isFavourite) {
        film.isFavourite = true;
        this.favourites.push(film);
      }
    }
  }

  findFavourites(): Film[] {
    return this.favourites;
  }

  getAllComments(filmId: number){
    const film = this.films.find(f => f.id === filmId);
    if(!film){
      return [];
   }
   return film.comments;
  }

}
